import { Link } from 'react-router-dom'
import { useAuth } from '../context/AuthContext.jsx'
import LogoutButton from './LogoutButton.jsx'

export default function Navbar(){
	const { user, authenticated } = useAuth()
	return (
		<nav className="navbar navbar-expand-lg bg-body-tertiary shadow-sm mb-3" style={{background:'#fff'}}>
			<div className="container">
				<Link className="navbar-brand" to="/">
					<i className="fas fa-chart-line me-2"></i>ASDP
				</Link>
				<div className="ms-auto d-flex align-items-center gap-2">
					{authenticated ? (
						<>
							<span className="badge bg-light text-dark">
								<i className="fas fa-user me-1"></i>{user.username}
							</span>
							<Link className="btn btn-outline-secondary btn-sm" to="/profile">
								<i className="fas fa-user-cog me-1"></i>Profile
							</Link>
							{user.role === 'admin' && (
								<Link className="btn btn-warning btn-sm" to="/admin">
									<i className="fas fa-shield-alt me-1"></i>Admin
								</Link>
							)}
							<LogoutButton 
								className="btn btn-outline-danger btn-sm"
								showConfirmation={true}
								redirectTo="/"
							/>
						</>
					) : (
						<>
							<Link className="btn btn-light btn-sm" to="/login">
								<i className="fas fa-sign-in-alt me-1"></i>Login
							</Link>
							<Link className="btn btn-outline-primary btn-sm" to="/register">
								<i className="fas fa-user-plus me-1"></i>Register
							</Link>
							<Link className="btn btn-warning btn-sm" to="/admin">
								<i className="fas fa-shield-alt me-1"></i>Admin
							</Link>
						</>
					)}
				</div>
			</div>
		</nav>
	)
}










